import React, { useState } from 'react';
import {RecipeForm} from "./RecipeForm";

const divStyle= {
    width: 200,
    height: 200,
    backgroundColor: "red",
}
const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    const [activeNewRecipe, setActiveNewRecipe] = useState(false);
    const [activeRecipe, setActiveRecipe] = useState(false);
    const [name, setName] = useState("");
    const [alc, setAlc] = useState("");
    console.log(activeRecipe)
    console.log(activeNewRecipe)


    const handleClick = (e) => {
        e.preventDefault()
        setActiveNewRecipe((prevState) => !prevState);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setActiveNewRecipe((prevState) => !prevState)
        setRecipe((prevState)=>[...prevState,{name, alc}])
    };
    const handleClickRecipe = (e) => {
        setActiveRecipe((prevState) => !prevState);

    }
    if (activeNewRecipe === true)
        return(
        <form onSubmit={handleSubmit}>
            <input type="text" value={name} onChange={e => setName(e.target.value)}/>
            <input type="text" value={alc} onChange={e => setAlc(e.target.value)}/>
            <button type={"submit"}>dodaj</button>
        </form>
    )
        else(activeRecipe === true)
            return (
                <>
                    <button onClick={handleClick}>Close</button>
                </>
            )
    return (
            <>
                <div style={divStyle} onClick={handleClick}>Dodaj Przepis!</div>
                {recipes.map((recipe, index) => (
                    <div
                        key={index}
                        style={{
                            width: 200,
                            height: 200,
                            backgroundColor: "red",
                        }}
                        onClick={handleClickRecipe}
                    >
                        <h1>{recipe.name}</h1>
                    </div>
                ))}
            </>
        );
    }

export { Recipe };
