import React, { useState } from 'react';

const Recipe = () => {
    const [boxes, setBoxes] = useState([]);

    const handleClick = () => {
        const randomColor = '#' + Math.floor(Math.random() * 16777215).toString(16);

        setBoxes((prevBoxes) => [...prevBoxes, randomColor]);
    };

    return (
        <>
            <div  onClick={handleClick}>Dodaj Boxa!</div>
            {boxes.map((box, index) => (
                <div
                    key={index}
                    style={{
                        width: 200,
                        height: 200,
                        backgroundColor: box,
                    }}
                />
            ))}
        </>
    );
};

export { Recipe };
