import React, { useState } from 'react';


const divStyle= {
    width: 200,
    height: 200,
    backgroundColor: "red",
}
const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    console.log(useState())

    const handleClick = () => {
        const newRecipe = {
            name: prompt("set name")
        };
        setRecipe((prevRecipe) => [...prevRecipe, newRecipe]);
    };

    return (
        <>
            <div style={divStyle} onClick={handleClick}>Dodaj Boxa!</div>
            {recipes.map((recipe, index) => (
                <div
                    key={index}
                    style={{
                        width: 200,
                        height: 200,
                        backgroundColor: "red",
                    }}
                > {recipes.name} </div>
            ))}
        </>
    );
};

export { Recipe };
