import React, { useState } from 'react';
import Modal from 'react-modal';



const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    const [activeNewRecipe, setActiveNewRecipe] = useState(false);
    const [activeRecipe, setActiveRecipe] = useState(false);
    const [name, setName] = useState("");
    const [ingName, setIngName] = useState("");
    const [ingreedients, setIngreedients] = useState([]);
    const recipe = ingreedients.reduce((o, key) => ({ ...o, [key]: key}), {})
    const [modalIsOpen, setIsOpen] = React.useState(false);
    function openModal() {
        setIsOpen(true);
    }
    function closeModal() {
        setIsOpen(false);
    }

    console.log(activeRecipe)
    console.log(activeNewRecipe)
    function compare(a, b) {
        if ( a.name < b.name){
            return -1;
        }}
    recipes.sort(compare)
    const handleClick = (e) => {
        e.preventDefault()
        setActiveNewRecipe((prevState) => !prevState);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setActiveNewRecipe((prevState) => !prevState)
        setRecipe((prevState)=>[...prevState, {name, recipe}])

    };
    const handleClickIng = (e) => {
        e.preventDefault()
        setIngreedients((preState)=>[...preState,ingName])
    }
    const handleClickRecipe = (e) => {
        e.preventDefault()
        setActiveRecipe((prevState) => !prevState);

    }
    console.log(recipes)
    console.log(ingreedients)
    // if (activeNewRecipe === true)
    //     return(
    //         <section className={"container"}>
    //         <form className={"form"} onSubmit={handleSubmit}>
    //             <input className={"new-recipe-title"} placeholder={'Nazwa'} onChange={e => setName(e.target.value)}/>
    //             <input className={"new-recipe-title"} placeholder={'składnik 1'} onChange={e => setIngName(e.target.value)}/>
    //             {ingreedients.map((item,index)=>(
    //                     <input key={index} className={"new-recipe-title"} placeholder={`składnik ${index+2}`} onChange={e => setIngName(e.target.value)}/>
    //             ))}
    //             <button onClick={handleClickIng}>dodaj składnik</button>
    //             <button type={"submit"}>dodaj</button>
    //
    //         </form>
    //         </section>
    //     )
    return (
        <section className={"container"}>

            {recipes.map((recipe, index) => (
                <div key={index} className={"recipe"} onClick={handleClickRecipe}>
                    <h2>{recipe.name}</h2>
                </div>
            ))}
            <div className={"recipe"} onClick={openModal}><h1>Dodaj Przepis!</h1></div>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
            >
                <section className={"container"}>
                    <form className={"form"} onSubmit={handleSubmit}>
                        <input className={"new-recipe-title"} placeholder={'Nazwa'} onChange={e => setName(e.target.value)}/>
                        <input className={"new-recipe-title"} placeholder={'składnik 1'} onChange={e => setIngName(e.target.value)}/>
                        {ingreedients.map((item,index)=>(
                            <input key={index} className={"new-recipe-title"} placeholder={`składnik ${index+2}`} onChange={e => setIngName(e.target.value)}/>
                        ))}
                        <button onClick={handleClickIng}>dodaj składnik</button>
                        <button type={"submit"}>dodaj</button>

                    </form>
                </section>
            </Modal>
        </section>
    );
}

export { Recipe };
