import React, { useState } from 'react';
import {RecipeForm} from "./RecipeForm";

const divStyle= {
    width: 200,
    height: 200,
    backgroundColor: "red",
}
const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    const [activeNewRecipe, setActiveNewRecipe] = useState(false);
    const [activeRecipe, setActiveRecipe] = useState(false);
    const [name, setName] = useState("");
    const [alc, setAlc] = useState("");
    const [ingreedients, setIngreedients] = useState({
        name,
    });
    console.log(activeRecipe)
    console.log(activeNewRecipe)
    function compare(a, b) {
        if ( a.name < b.name){
            return -1;
        }}
    recipes.sort(compare)
    const handleClick = (e) => {
        e.preventDefault()
        setActiveNewRecipe((prevState) => !prevState);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setActiveNewRecipe((prevState) => !prevState)
        setRecipe((prevState)=>[...prevState,{name, alc}])

    };
    const handleClickIng = () => {
        setIngreedients((prevState)=>[...prevState])
    }
    const handleClickRecipe = (e) => {
        e.preventDefault()
        setActiveRecipe((prevState) => !prevState);

    }
    console.log(recipes)
    console.log(ingreedients)
    if (activeNewRecipe === true)
        return(
            <form onSubmit={handleSubmit}>

                {ingreedients.map((ingreedient, index) => (
                    <input key={index}/>
                ))}
                <button type={"submit"}>dodaj</button>
                <button onClick={handleClickIng}>dodaj sk≈Çadnik</button>

            </form>
        )
    else if(activeRecipe === true)
        return(
            <form onSubmit={handleClickRecipe}>
                <input type="number" placeholder={name}/>
            </form>
        )

    return (
        <>
            <div style={divStyle} onClick={handleClick}>Dodaj Przepis!</div>
            {recipes.map((recipe, index) => (
                <div key={index} style={{width: 200, height: 200, backgroundColor: "red",}} onClick={handleClickRecipe}>
                    <h1>{recipe.name}</h1>
                </div>
            ))}
        </>
    );
}

export { Recipe };
