    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(name);
    };


    return(
      <form onSubmit={handleSubmit}>
        <label>Wiadomość:
          <textarea value={name} onChange={handleChange}/>
        </label>
        <input type="submit" value="Wyślij"/>
      </form>
  )
}

