import React, {useEffect, useState,} from 'react';
import Modal from 'react-modal';
import {RecipeDetails} from "./RecipeDetails";
//uuid


const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    const [openedRecipe, setOpenRecipe] = useState(null);
    const [name, setName] = useState("");
    const [process, setProcess] = useState("");
    const [ingName, setIngName] = useState("");
    const [ingValue, setIngValue] = useState("");
    const [ingredients, setIngredients] = useState([]);
    const [ingredientsValue, setIngredientsValue] = useState([]);
    const [newRecipeModalIsOpen, setNewRecipeModalIsOpen] = React.useState(false);
    const [recipeModalIsOpen, setRecipeModalIsOpen] = React.useState(false);

    const recipe =  ingredientsValue.reduce(function(recipe, field, index) {
        recipe[ingredients[index]] = field;
        return recipe;
    }, {})

    function compare(a, b) {
        if ( a.name < b.name){
            return 1;
        }}
    recipes.sort(compare)

    const handleClickIng = (e) => {
        e.preventDefault()
        setIngredients((preState)=>[...preState,ingName])
        setIngredientsValue((preState)=>[...preState,ingValue])
    }
    function openNewModal() {
        setNewRecipeModalIsOpen(true);
    }
    function closeNewModal(e) {
        e.preventDefault()
        setNewRecipeModalIsOpen(false);
        setRecipe((prevState)=>[...prevState, {name, recipe, process}])
        setIngredients((prevState) => [])
        console.log(recipes)
    }
    function openModal(recipe) {
        setOpenRecipe(recipe);
        setRecipeModalIsOpen(true);
    }
    function closeModal(close) {
        setRecipeModalIsOpen(false);
    }
    // console.log(recipes)
    // console.log(openedRecipe)
    // console.log(ingName)
    return (
        <>
            {/*<header>*/}
            {/*    <h1>BOOZE COUNTER</h1>*/}
            {/*</header>*/}
            <section className={"container"}>
                <div className={"recipe"} onClick={openNewModal}>
                    <h1>Dodaj Przepis!</h1>
                </div>
                {recipes.map((recipe, index) => (
                    <div key={index} className={"recipe"} onClick={ () => openModal(recipe)}>
                        <h2>{recipe.name}</h2>
                    </div>
                ))}
                <Modal
                    isOpen={recipeModalIsOpen}
                    onRequestClose={closeModal}
                    ariaHideApp={false}
                >
                    <RecipeDetails recipe={openedRecipe} close={closeModal}/>
                </Modal>
                <Modal
                    isOpen={newRecipeModalIsOpen}
                    onRequestClose={closeNewModal}
                    ariaHideApp={false}
                >
                    <section className={"container"}>
                        <form className={"form"} onSubmit={closeNewModal}>
                            <input className={"new-recipe-title"} placeholder={'Nazwa'} onChange={e => setName(e.target.value)}/>
                            <div className={'new-ing-div'}>
                                <input className={"new-recipe-title"} placeholder={'składnik 1'} onChange={e => setIngName(e.target.value)}/>
                                <input type={'number'} className={"new-recipe-title"} placeholder={`quantity`} onChange={e => setIngValue(e.target.value)}/>
                            </div>

                            {ingredients.map((item,index)=>(
                                <div key={index} className={'new-ing-div'}>
                                    <input  className={"new-recipe-title"} placeholder={`składnik ${index+2}`} onChange={e => setIngName(e.target.value)}/>
                                    <input type={'number'} className={"new-recipe-title"} placeholder={"quantity"} onChange={e => setIngValue(e.target.value)}/>
                                </div>
                            ))}
                            <textarea className={"new-recipe-title"} onChange={e => setProcess(e.target.value)}/>
                            <button onClick={handleClickIng}>dodaj składnik</button>
                            <button type={"submit"}>dodaj</button>

                        </form>
                    </section>
                </Modal>
            </section>
        </>
    );
}

export { Recipe };
