        <section className={"container"}>
            <div className={"recipe"} onClick={openModal}><h1>Dodaj Przepis!</h1></div>
            {recipes.map((recipe, index) => (
                <div key={index} className={"recipe"} onClick={handleClickRecipe}>
                    <h2>{recipe.name}</h2>
                </div>
            ))}
            <RecipeForm/>

        </section>
    );
}

