import React, { useState } from 'react';
import {RecipeForm} from "./RecipeForm";

const divStyle= {
    width: 200,
    height: 200,
    backgroundColor: "red",
}
const Recipe = () => {
    const [recipes, setRecipe] = useState([]);
    const [active, setActive] = useState(false);
    const [name, setName] = useState("");
    const [alc, setAlc] = useState("");


    const handleClick = (e) => {
        e.preventDefault()
        setActive((prevState) => !prevState);
    }
    console.log(recipes)
    const handleSubmit = (e) => {
        e.preventDefault();
        setActive((prevState) => !prevState)
        setRecipe((prevState)=>[{...prevState},{name, alc}])
    };
    if (active === true)
        return(
        <form onSubmit={handleSubmit}>
            <input type="text" value={name} onChange={e => setName(e.target.value)}/>
            <input type="text" value={alc} onChange={e => setName(e.target.value)}/>
        </form>
    )
    else
    return (
            <>
                <div style={divStyle} onClick={handleClick}>Dodaj Boxa!</div>
                {recipes.map((recipe, index) => (
                    <div
                        key={index}
                        style={{
                            width: 200,
                            height: 200,
                            backgroundColor: "red",
                        }}
                    > {recipe} </div>
                ))}
            </>
        );
    }

export { Recipe };
