import React, {useState} from "react";
import {Ingredient} from "./models/ingredient";

const glassType = [{
        name: "shot glass",
        size: 40,
    },
    {
        name: "long glass",
        size: 310
    }
]


const RecipeDetails = ({recipe, close, deleteRecipe}) => {

    const [newQ, setNewQ] = useState([new Ingredient("","")])
    const [sumQ, setSumQ] = useState(0)
    // console.log(recipe.ingredients)
    console.log(recipe)
    const handleDelete =()=>{
        deleteRecipe(recipe.id)
        close()
    }
    // const updateSum = (newQuantity) =>{
    //     newQuantity.map
    // }
    const updateIngValue = (index, newQuantity) =>{
        const ingredient = new Ingredient("", "");
        setNewQ( prevState => [...prevState, ingredient]);
        const updatedIngredients = [...newQ];
        updatedIngredients[index].quantity= newQuantity;
        setNewQ(updatedIngredients);
    }
    console.log(newQ)

    return(
        <>
            <h2> Prepare Your {recipe.name}!</h2>
            <h3>How Many Drinks You want to do?</h3>

            <form key={recipe.id} className={"form"} onSubmit={close}>
                <input type="number" placeholder='set Value'/>
                <h3>What type of Glass You Have?</h3>
                <select>
                    {glassType.map((glass,index) => (
                        <option>{glass.name} {glass.size}.ml</option>
                    ))}
                </select>
                <h4>You Need</h4>
                {recipe.ingredients.map((ingredient,index) => (
                    <h5>{ingredient.name} </h5>
                ))}
                <button key={recipe.id} onClick={handleDelete}>Delete Recipe</button>
            </form>
            <p>{recipe.process}</p>
        </>
    )
}

export {RecipeDetails}
