import React, { useState } from 'react';
import Modal from 'react-modal';
import {RecipeDetails} from "./RecipeDetails";
import {NewRecipe} from "./NewRecipe";
//uuid


const Recipe = () => {
    const [newRecipe] = useState({})
    const [recipes, setRecipe] = useState([]);
    const [openedRecipe, setOpenRecipe] = useState(null);
    const [name, setName] = useState("");
    const [ingName, setIngName] = useState("");
    const [ingreedients, setIngreedients] = useState([]);
    const recipe = ingreedients.reduce((o, key) => ({ ...o, [key]: key}), {})
    const [newRecipeModalIsOpen, setNewRecipeModalIsOpen] = React.useState(false);
    const [recipeModalIsOpen, setRecipeModalIsOpen] = React.useState(false);
    function compare(a, b) {
        if ( a.name < b.name){
            return -1;
        }}
    recipes.sort(compare)

    const handleClickIng = (e) => {
        e.preventDefault()
        setIngreedients((preState)=>[...preState,ingName])
    }
    function openNewModal() {
        setNewRecipeModalIsOpen(true);
    }
    function closeNewModal() {
        setNewRecipeModalIsOpen(false);
        setRecipe((prevState)=>[...prevState, {name, recipe}])
    }
    function openModal(recipe) {
        setOpenRecipe(recipe);
        setRecipeModalIsOpen(true);
    }
    function closeModal(close) {
        setRecipeModalIsOpen(false);
    }
    console.log(openedRecipe)

    return (
        <>
            {/*<header>*/}
            {/*    <h1>BOOZE COUNTER</h1>*/}
            {/*</header>*/}
            <section className={"container"}>
                <div className={"recipe"} onClick={openNewModal}>
                    <h1>Dodaj Przepis!</h1>
                </div>
                {recipes.map((recipe, index) => (
                    <div key={index} className={"recipe"} onClick={ () => openModal(recipe)}>
                        <h2>{recipe.name}</h2>
                    </div>
                ))}
                <Modal
                    isOpen={recipeModalIsOpen}
                    onRequestClose={closeModal}
                    ariaHideApp={false}
                >
                    <RecipeDetails recipe={openedRecipe} close={closeModal}/>
                </Modal>
                <Modal
                    isOpen={newRecipeModalIsOpen}
                    onRequestClose={closeNewModal}
                    ariaHideApp={false}
                >
                    <NewRecipe newRecipe={newRecipe} />
                </Modal>
            </section>
        </>
    );
}

export { Recipe };
